function window = initScreen[window(1), rect] = SCREEN(0, 'OpenWindow', [128, 128, 128]);% mid-gray blank screenwindow(2) = SCREEN(window(1), 'OpenOffscreenWindow', [128, 128, 128], rect);% mid-gray fixation screenwindow(3) = SCREEN(window(1), 'OpenOffscreenWindow', [128, 128, 128], rect);centered = centerrect([0, 0, 30, 30], rect);SCREEN(window(3),'FrameOval',[0, 255, 0], centered, 5 , 5);% temp off-screenwindow(4) = SCREEN(window(1), 'OpenOffscreenWindow', [128, 128, 128], rect);