function data = makeData(fact)% MAKEDATA.% The seed is always in the first column.nn = length(fact);nbData = 1; for ii = 1:nn, nbData = nbData * fact(ii); enddata = zeros(nn+1, nbData);rand('state',sum(100*clock));data(1,:) = round(2^24*rand(1, nbData)); %the seedstemp = sprintf('marker = 1;\n');for ii = 1:nn, temp = [temp, sprintf('for vari%d = 1:fact(%d),\n', ii, ii)]; endfor ii = 1:nn, temp = [temp, sprintf('data(1+%d, marker) = vari%d;\n', ii, ii)]; endtemp = [temp, sprintf('marker = marker + 1;\n')];for ii = 1:nn, temp = [temp, sprintf('end\n')]; endeval(temp)index = randperm(nbData);data = data(:, index);