% 1.	noise = 'NoiseFrequencyTime.m';% 2.	noise = 'NoiseFrequency.m';% 3.	noise = 'noiseFreqAdd.m';% 4. 	noise = 'noiseRevCorrND.m';% 5. 	noise = 'noiseLocationND.m';whichNoise = 3;switch whichNoisecase 1			%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%	% 1.0 noise = 'NoiseFrequencyTime.m';	noise = 'NoiseFrequencyTime.m';	constants = 'glopar{1}=[256, 256, 18]; glopar{2}=[8 2.74];glopar{3}=''uniform'';glopar{4}=.01;glopar{5}=0.8;glopar{6}=''K=anImage(1,1);'';';	data = MAKEDATA([4, 4]);	% 2 VD avec 4 conditions	data(4, :) = 2; data(4, lt(data(2, :), 11)) = 1;	MAKECID('demoSTRF.pcid', noise, constants, data);	clear all		% Use a PCID to create an image	cid = READCID('demoSTRF.pcid');	anImage = stretch(IMREAD('w1h.jpg'));	% anImage = stretch(IMREAD('p001.tiff'));	theStimulus = MAKETHENOISE(cid.data(1,1), anImage);	theNoise = MAKETHENOISE(cid.data(1,1));	close	figure,showIm(theNoise)	figure, 	for i = 1 : 18		% subplot(4,4,i),showIm(theStimulus(:,:,i))		showIm(theStimulus(:,:,i));drawnow	end;case 2	%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%	% 2.0 noise = 'NoiseFrequency.m';	noise = 'NoiseFrequency.m';	constants = 'glopar{1}=[256, 256]; glopar{2}=8;glopar{3}=''gaussian'';glopar{4}=.1;glopar{5}=0.8;glopar{6}=''reflect'';';	data = MAKEDATA([4, 4]);	% 2 VD avec 4 conditions	data(4, :) = 2; data(4, lt(data(2, :), 11)) = 1;	MAKECID('demoSRF.pcid', noise, constants, data);	clear all		% Use a PCID to create an image	cid = READCID('demoSRF.pcid');	imageName = 'p012.tiff'; % 'w1h.jpg'; % 	anImage = stretch(IMREAD(imageName));	theStimulus = MAKETHENOISE(cid.data(1,1), anImage);	theNoise = MAKETHENOISE(cid.data(1,1));		figure,bar(theNoise),colormap(jet);axis xy;0	figure, showIm(theStimulus);	case 3	%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%	% 3.0 noise = 'noiseFreqAdd.m';	noise = 'noiseFreqAdd.m';	constants = 'glopar{1}=[256, 256]; glopar{2}=''uniform'';glopar{3}=1;glopar{4}=1;glopar{5}=''add'';glopar{6}= 1./[1:128];';	data = MAKEDATA([4, 4]);	% 2 VD avec 4 conditions	data(4, :) = 2; data(4, lt(data(2, :), 11)) = 1;	MAKECID('demoFreqAdd.pcid', noise, constants, data);	clear all		% Use a PCID to create an image	cid = READCID('demoFreqAdd.pcid');	imageName = 'p012.tiff'; % 'w1h.jpg'; % 	anImage = stretch(IMREAD(imageName));	theStimulus = MAKETHENOISE(cid.data(1,1), anImage);	theNoise = MAKETHENOISE(cid.data(1,1));	figure,showIm(anImage);	figure,	subplot(121),showIm(theNoise);	subplot(122),showIm(theStimulus);	case 4	%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%	% 3.0 noise = 'noiseRevCorrND.m';	noise = 'noiseRevCorrND.m';	constants = 'glopar{1}=[256, 256];glopar{2}=0.5;glopar{3}=''uniform'';glopar{4}=1;glopar{5}=''add'';';	data = MAKEDATA([4, 4]);	% 2 VD avec 4 conditions	data(4, :) = 2; data(4, lt(data(2, :), 11)) = 1;	MAKECID('demoRevCorr.pcid', noise, constants, data);	clear all		% Use a PCID to create an image	cid = READCID('demoRevCorr.pcid');	imageName = 'p012.tiff'; % 'w1h.jpg'; % 	anImage = stretch(IMREAD(imageName));	theStimulus = MAKETHENOISE(cid.data(1,1), anImage);	theNoise = MAKETHENOISE(cid.data(1,1));	%figure,showIm(anImage);	figure,	subplot(121),showIm(theNoise);	subplot(122),showIm(theStimulus);	case 5		%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%	% 5.0 noise = 'noiseLocationND.m';		noise = 'noiseLocationND.m';	constants = 'glopar{1}=[256 256];glopar{2}=[10,10];glopar{3}=16;';	% 	constants = 'glopar{1}=256;glopar{2}=10;glopar{3}=''14'';';	data = MAKEDATA([4, 4]);	% 2 VD avec 4 conditions	data(4, :) = 2; data(4, lt(data(2, :), 11)) = 1;	MAKECID('demoLocND.pcid', noise, constants, data);	clear all		% Use a PCID to create an image	cid = READCID('demoLocND.pcid');	imageName =  'p012.tiff'; % 'w1h.jpg'; %	anImage = stretch(IMREAD(imageName));	% anImage = rand(256,256,3)*255;		theStimulus = MAKETHENOISE(cid.data(1,1), anImage);	theNoise = MAKETHENOISE(cid.data(1,1));		figure;subplot(121),showIm(theStimulus)	subplot(122),showIm(theNoise)		% 	for i = 1 :  max(size(size(anImage)))	% 		subplot(2,3,i);showIm(theStimulus(:,:,i))	% 		subplot(2,3,3+i);showIm(theNoise(:,:,i))	% 	end;end;