% 1.	noise = 'NoiseFrequencyTime.m';% 2.	noise = 'NoiseFrequency.m';% 3.	noise = 'noiseFreqAdd.m';	% not implemented% 4. 	noise = 'locationNoiseND.m';whichNoise = 2switch whichNoisecase 1			%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%	% 1.0 noise = 'NoiseFrequencyTime1D.m';	noise = 'NoiseFrequencyTime1D.m';	constants = 'glopar{1}=256;glopar{2}=18;glopar{3}=[8 2.74],glopar{4}=0.01,glopar{5}=''reflect'',glopar{6}=''sparse''';	data = MAKEDATA([4, 4]);	% 2 VD avec 4 conditions	data(4, :) = 2; data(4, lt(data(2, :), 11)) = 1;	MAKECID('demoSTRF.pcid', noise, constants, data);	clear all		% Use a PCID to create an image	cid = READCID('demoSTRF.pcid');	anImage = rand(1,256);	theStimulus = MAKETHENOISE(cid.data(1,1), anImage);	theNoise = MAKETHENOISE(cid.data(1,1));		figure,	subplot(121),showIm(theNoise');axis xy	subplot(122),showIm(theStimulus');axis xy;colormap(jet)case 2	%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%	% 2.0 noise = 'NoiseFrequency1D.m';	noise = 'NoiseFrequency1D.m';	constants = 'glopar{1}=256;glopar{2}=8;glopar{3}=.1,glopar{4}=0.8,glopar{5}=''reflect'';glopar{6}=''sparse''';	data = MAKEDATA([4, 4]);	% 2 VD avec 4 conditions	data(4, :) = 2; data(4, lt(data(2, :), 11)) = 1;	MAKECID('demoSRF.pcid', noise, constants, data);	clear all		% Use a PCID to create an image	cid = READCID('demoSRF.pcid');	anImage = rand(1,256);	theStimulus = MAKETHENOISE(cid.data(1,1), anImage);	theNoise = MAKETHENOISE(cid.data(1,1));		figure,	subplot(211),bar(theNoise),colormap(jet);axis xy;	subplot(212),plot(theStimulus);	case 3	%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%	% 3.0 noise = 'noiseWhite1D.m';	noise = 'noiseWhite1D.m';	constants = 'glopar{1}=256;glopar{2}=.5;';	data = MAKEDATA([4, 4]);	% 2 VD avec 4 conditions	data(4, :) = 2; data(4, lt(data(2, :), 11)) = 1;	MAKECID('demoWhite1D.pcid', noise, constants, data);	clear all		% Use a PCID to create an image	cid = READCID('demoWhite1D.pcid');	anImage = ones(1,256);	theStimulus = MAKETHENOISE(cid.data(1,1), anImage);	theNoise = MAKETHENOISE(cid.data(1,1));	figure,	subplot(211),plot(theNoise);	subplot(212),plot(theStimulus);	% case 3% % % 	%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 	% 3.0 noise = 'noiseFreqAdd.m';% 	noise = 'noiseFreqAdd.m';% 	constants = 'glopar{1}=256;glopar{2}=256;glopar{3}=0.1;glopar{4}=''gaussian'';glopar{5}=''add'';glopar{6}= 1./[1:128];';% 	data = MAKEDATA([4, 4]);	% 2 VD avec 4 conditions% 	data(4, :) = 2; data(4, lt(data(2, :), 11)) = 1;% 	MAKECID('demoFreqAdd.pcid', noise, constants, data);% 	clear all% 	% 	% Use a PCID to create an image% 	cid = READCID('demoFreqAdd.pcid');% 	imageName = 'p012.tiff'; % 'w1h.jpg'; % % 	anImage = stretch(IMREAD(imageName));% 	theStimulus = MAKETHENOISE(cid.data(1,1), anImage);% 	theNoise = MAKETHENOISE(cid.data(1,1));% 	size(theNoise)% 	figure,showIm(theNoise);% 	figure, showIm(theStimulus);	case 4	%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%	% 5.0 noise = 'locationNoiseND.m';		noise = 'locationNoiseND.m';	constants = 'glopar{1}=256;glopar{2}=20;glopar{3}=''16'';';	% 	constants = 'glopar{1}=256;glopar{2}=10;glopar{3}=''14'';';	data = MAKEDATA([4, 4]);	% 2 VD avec 4 conditions	data(4, :) = 2; data(4, lt(data(2, :), 11)) = 1;	MAKECID('demoLocND.pcid', noise, constants, data);	clear all		% Use a PCID to create an image	cid = READCID('demoLocND.pcid');	anImage = rand(1,256)*255;		theStimulus = MAKETHENOISE(cid.data(1,1), anImage);	theNoise = MAKETHENOISE(cid.data(1,1));		subplot(121),plot(theStimulus)	subplot(122),plot(theNoise)	end;