function [theKey, rt] = shownrec(window, anImage, respSpectrum)SCREEN(window(2), 'PutImage', anImage)rect = SCREEN(window(2), 'Rect');rectCentre = centerRect(rect, SCREEN(window(1),'Rect'));SCREEN('CopyWindow', window(2), window(1), rect, rectCentre, 'srcCopyQuickly');SCREEN(window(1), 'WaitBlanking');startSecs = GetSecs;while 1	[keyIsDown,endSecs,keyCode] = KbCheck;	if keyIsDown		theKey = KbName(keyCode);		if ~isempty(findstr(theKey, respSpectrum))			break		end	endendFlushEvents('keyDown');rt = endSecs - startSecs;