function Res = StatThresh(ZSCi,p,sigma_b,tC,S_r);FWHM = HalfMax(sigma_b);		% computes the full width half maximumif exist('S_r')	D = length(size(S_r)); 					% number of dimensions in the search space	[volumes, N] = CiVol(sum(S_r(:)), D) 	% computes the intrinsic volumeselse	D = length(size(ZSCi)); 					% number of dimensions in the search space	[volumes, N] = CiVol(sum(ZSCi(:)), D) 	% computes the intrinsic volumesend;	[tP, k] = stat_threshold(volumes, N, FWHM, Inf, p, tC);	 % the actual statistical testsRes.ZSCi = ZSCi;Res.tP = tP;Res.p = p;Res.tC = tC;Res.k = k;Res.FWHM = FWHM;