function varargout = noiseWhite1D(varargin)global gloparif (length(glopar)~=2)	error(sprintf('glopar(1) = size along the X axis\n\glopar(2) = signal-to-noise ratio\nMust be defined in the CID.'));endoneTrial = varargin{1};rand('state', oneTrial(1));xSize = glopar{1};theNoise = randn(1, xSize);theNoise = min(max(theNoise / (2*2.33), -.5), .5);	% why min max ?if (nargin == 1)	varargout{1} = theNoise;	returnend;s2n = glopar{2};anImage = double(varargin{2});anImage = (anImage - min(anImage(:))) / (max(anImage(:)) - min(anImage(:)));varargout{1} = s2n * (anImage - .5) + (1 - s2n) * theNoise + .5;