function out = resultPyramid(ci, background)nBands = 6;imSubSample=0;nBands = 6;fGauss = mkGaussian(11);fGauss1 = fGauss(:,1);fGauss1 = sqrt(2)*fGauss1/sum(fGauss1);clear fGauss;nPeriod = 1;nZero = 2.18;out = zeros(size(background));[pyr,pind] = buildLpyr(double(background),nBands-imSubSample,fGauss1);temp = zeros(size(background));for ii = 1:nBands-1,	sigma = nPeriod * 2^ii	TNoyau = 6*sigma;	h = fspecial('gaussian',ceil(TNoyau),sigma);	temp = stretch(filter2(h,ci(:,:,ii)));	out = double(out + double(reconLpyr(pyr,pind,[ii],fGauss1) .* temp));endwinImage = double(out + double(reconLpyr(pyr,pind,[nBands],fGauss1)));out = uint8(255*stretch(out));