function aCell = segmentStr(aString, separator)marker = 0;temp = findstr(aString, separator);str = aString(1:temp(1)-1);if ~isempty(str)    marker = marker + 1;    aCell{marker} = str;endfor ii = 2:length(temp),    str = aString(temp(ii-1)+1:temp(ii)-1);    if ~isempty(str)        marker = marker + 1;    	aCell{marker} = str;    endendstr = aString(temp(end)+1:end);if ~isempty(str)    marker = marker + 1;    aCell{marker} = str;end